mapper.select:
SELECT COUNT(*) 
FROM task_conn
WHERE task_id=#{id}

mapper.update:
UPDATE task_conn
SET root_task_id=#{taskId}, ref_level=ref_level+1
WHERE root_task_id=#{preTaskId}

UPDATE task_conn
SET root_task_id=#{headId}, ref_level=ref_level+1
WHERE root_task_id=#{taskId}

UPDATE task_conn
SET ref_level=ref_level-1
WHERE root_task_id=#{headId}
